import styled from 'styled-components';

export const Container = styled.div`
  background-color: #2f3136;
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;

  main {
    background-color: #36393f;
    min-width: 350px;
    border-radius: 0px 4px 0px 12px rgba(0,0,0,0.1);
    padding: 24px;
    display: flex;
    align-items: center;
    flex-direction: column;

    h1 {
      color: #fff;
      font-weight: bold;
      font-size: 1.5rem;
    }

    h4 {
      color: #8e8f81;
      font-weight: 400;
      font-size: 1rem;
    }

    .input {
      display: flex;
      flex-direction: column;
      width: 100%;

      label {
        color: #8e8f81;
      }

      input {
        border: none;
        outline: none;
        width: 100%;
      }
    }

    button {
      border: none;
      outline: none;
      width: 100%;
      background-color: #7289da;
      color: #fff;
      font-weight: bold;
      padding: 12px 6px;
      font-size: 1rem;
      cursor: pointer;
    }
  }
`;
