import styled from 'styled-components';
import { MdKeyboardArrowDown, MdMic, MdHeadset } from 'react-icons/md'
import { FaCog } from 'react-icons/fa'

export const Container = styled.div`
  display: grid;
  grid-template-rows: 48px 1fr 52px;
  height: 100vh;
  width: 100%;
`;

export const HeaderSidebar = styled.div`
  background-color: #2E3036;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem;
  border-bottom: 1px solid #222327;

  h1 {
    font-size: 1rem;
    color: #fff;
  }
`;

export const ArrowDown = styled(MdKeyboardArrowDown)`
  font-size: 1.6rem;
  color: #fff;
`; 

export const SubChats = styled.div`
  background-color: #222327;
`;

export const UserContainer = styled.div`
  display: flex;

  img {
    height: 32px;
    width: 32px;
    border-radius: 50%;
    object-fit: cover;
  }
`;

export const IconMic = styled(MdMic)``;
export const IconHeadset = styled(MdHeadset)``;
export const IconCog = styled(FaCog)``;
