import styled from 'styled-components';

export const Container = styled.div`
  background-color: #2f3136;
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;

  main {
    background-color: #36393f;
    min-width: 340px;
    border-radius: 0px 4px 0px 12px rgba(0,0,0,0.1);
    padding: 12px;
    display: flex;
    align-items: center;
    flex-direction: column;

    h1 {
      color: #fff;
      font-weight: bold;
      font-size: 24px;
    }

    h4 {
      color: #8e8f81;
      font-weight: 400;
      font-size: 16px;
    }

    .input {
      label {
        color: #8e8f81;
      }
    }
  }
`;
