export const Container = styled.div`
  display: grid;
  grid-template-rows: 3rem 1fr 3.25;
  height: 100vh;
  width: 100%;
`;

export const HeaderChat = styled.div`

`;

export const InputChat = styled.div`

`;
export const IconGroup = styled(RiGroupFill)``;
